import React,{Component} from 'react';
import {Modal,Button,Row,Col,Form} from 'react-bootstrap';

export class EditEmpModel extends Component {
    constructor(props){
        super(props);
        this.handleSubmit=this.handleSubmit.bind(this);
    }
    handleSubmit(event){
        event.preventDefault();
        fetch(process.env.REACT_APP_API+'employee',{
            method:'PUT',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                DepartmentId:event.target.EmployeeId.value,
                DepartmentName:event.target.EmployeeName.value
            })
        })
        .then(res=>res.json())
        .then((result)=>{
            alert(result);
        },
        (error)=>{
            alert("Failed")
        })
    }
    render(){
        return (
            <div className="container">
                <Modal
                {...this.props}
                size="lg"
                aria-labelledby='contained-modal-title-vcenter'
                centered
                >
                    <Modal.Header closeButton>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Edit Employee
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Row>
                            <Col sm={6}>
                                <Form onSubmit={this.handleSubmit}>
                                <Form.Group controlId="DepartmentId">
                                        <Form.Label>DepartmmentId</Form.Label>
                                        <Form.Control type="text" name="DepartmentId" required placeholder="DepartmentId"
                                        disabled
                                        defaultValue={this.props.depid}
                                        />
                                    </Form.Group>
                                    <Form.Group controlId="DepartmentName">
                                        <Form.Label>DepartmmentName</Form.Label>
                                        <Form.Control type="text" name="DepartmentName" required placeholder="DepartmentName"
                                        defaultValue={this.props.depname}
                                        />
                                    </Form.Group>
                                    <Form.Group>
                                        <Button variant ="primary" type="submit">
                                            Update department
                                        </Button>
                                    </Form.Group>
                                </Form>
                            </Col>
                        </Row>
                    </Modal.Body>

                    <Modal.Footer>
                        <Button variant="danger" onClick={this.props.onHide}>Close</Button>

                    </Modal.Footer>

                </Modal>
            </div>
        )
    }
}
